/* "Colored Void"
 * I wanted to practice GLSL and the primitives describe in https://thebookofshaders.com/07/
 * so I started looking for art images to try to reproduce.
 * This one is the part of the serie, it is based on Lino Ruso work: 
 * https://www.behance.net/gallery/49992709/Blurld
 * (November 2023)
 */

vec3 circleWithBlur(in vec2 center, in float radius, in vec2 st, in vec3 color, in float blur){
    vec2 dist = st - center;
	return 1. - vec3(1.-smoothstep(radius-(radius*0.01),
                         radius+(radius * blur),
                         dot(dist,dist)*4.0)) + color;
}  

// Clamp the color to make sure it's between [0;1]
vec3 clampColor(in vec3 color){
    return vec3(clamp(color.x,0.,1.), clamp(color.y,0.,1.), clamp(color.z,0.,1.));
}

vec2 rotateAroundPoint(in vec2 rotCenter,in float angle, in vec2 p)
{
  float s = sin(angle);
  float c = cos(angle);

  // translate point back to origin:
  p -= rotCenter;

  // rotate point
  float xnew = p.x * c - p.y * s;
  float ynew = p.x * s + p.y * c;

  // translate point back:
  p.x = xnew + rotCenter.x;
  p.y = ynew + rotCenter.y;
  return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 st = gl_FragCoord.xy/iResolution.xy;
    float aspect_ratio = iResolution.x / iResolution.y;
    st.x *= aspect_ratio;

    // Don't forget to multiply the point by the aspect ratio to have the proper rendering
    // whatever the resolution
    vec2 rotationCenter = vec2(0.5*aspect_ratio,0.5);
    vec2 center = rotateAroundPoint(rotationCenter,0.5+iTime/1.3, vec2(0.52*aspect_ratio,(0.508)));
    vec3 c1 = circleWithBlur(center, 0.4, st, vec3(0.,0.,1.),0.346); // Blue

    center = rotateAroundPoint(rotationCenter,0.5-iTime/1.3, vec2(0.492*aspect_ratio,(0.524)));
    vec3 c2 = circleWithBlur(center, 0.4, st, vec3(1.,0.,0.),0.346); // Red

    center = rotateAroundPoint(rotationCenter,0.5-iTime/1.3, vec2(0.54*aspect_ratio,(0.540)));
    vec3 c3 = circleWithBlur(center, 0.192, st, vec3(0.,1.,1.),2.460); // Cyan

    center = rotateAroundPoint(rotationCenter,0.5+iTime/1.3, vec2(0.500*aspect_ratio,(0.476)));
    vec3 c4 = circleWithBlur(center, 0.428, st, vec3(1.,0.,1.),0.596); // Magenta

    center = rotateAroundPoint(rotationCenter,0.5-iTime/1.3, vec2(0.468*aspect_ratio,(0.540)));
    vec3 c5 = circleWithBlur(center, 0.308, st, vec3(1.,1.,0.),1.340); // Yellow

    vec3 color = clampColor(c1) * clampColor(c2) * clampColor(c3) * clampColor(c4) * clampColor(c5);

    fragColor = vec4(color,1.0);
}
