// reproducing Gianni Sarcone's https://twitter.com/gsarcone/status/1721681488628854791
// golfed version below

#define f(v)  sin( 27.*(v) )
#define S(v) smoothstep( -1., 1., ( f(v) -.7 ) / fwidth(f(v)) )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u - .5*R ) / R.y;                   // normalize coordinates
    float l = length(U), a = atan(U.y,U.x),         // polar coords
         s1 = S(a - l ),                            // still spirals
         s2 = S(a + l - .3*iTime );                 // rotating counter-spirals
    O = max( vec4(1,1,0,0) * max(s1,s2) ,           // yellow spirals
             s1 * s2 );                             // white intersection
}







/** // --- 206 chars by timestamp


#define f(v)  sin( 27.*( atan(U.y,U.x) - v )) //
#define S(v) smoothstep( -1., 1., ( f(v) -.7 ) / fwidth(f(v)) )

void mainImage( out vec4 O, vec2 U )
{
    O.xyz = iResolution;    
    float l = length( U -= .5*O.xy ) / O.y;    
    O += S( l ) + S( .3 * iTime + l ) - O;
    O.z--;
}

/**/





/** // --- 235/227 chars golfed version:

#define f(v)  sin( 27.*( atan(U.y,U.x) - v )) //
#define S(v) smoothstep( -1., 1., ( f(v) -.7 ) / fwidth(f(v)) )

void mainImage( out vec4 O, vec2 U )
{
    O.xyz = iResolution;
    U -= .5*O.xy;    
    float l = length(U) / O.y,
          a = S( l ),
          b = S( .3*iTime + l );
    O = max( vec4(1,1,0,0) * max(a,b) , a * b );
//  O =      vec4(1,1,0,0) * (a+b)    + a * b  ;  // -8ch, slightly different
}

/**/
