void mainImage( out vec4 pcol, in vec2 pcoord )
{
    vec2 uv = pcoord/iResolution.xy-0.5;
    vec2 um = abs(iMouse.xy/iResolution.xy-0.5);
    vec3 col;

    col = 0.05 + 0.2*cos(iTime+uv.xyx+vec3(0,2,4));
    //if (cos(0.37*uv.x*uv.y*200.+iTime*1.3)>0.9) {
    if ( cos(0.37*uv.x*1000.*pow(10.0,(um.x*um.y*40.))*uv.y+iTime*1.3) >0.9 ) {
    col = uv.yyx+vec3(0.8,0.5,0.5)+0.2;
    }
    pcol = vec4(col,1.0);
}
