float metaball(vec2 p, vec2 c, float r) {
    float dist = length(p - c);
    return r*r/(dist*dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0; // Transform the coordinate space to (-1, 1)

    // Mouse position normalized to -1 to 1
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = mouse * 2.0 - 1.0;

    // Metaball parameters
    vec2 pos1 = vec2(0.0, sin(iTime * 0.5));
    vec2 pos2 = vec2(0.0, sin(iTime * 0.2));
    float radius = 0.25;

    // Calculate the field strengths
    float mb1 = metaball(uv, pos1, radius);
    float mb3 = metaball(pos2, uv, radius);
    float mb2 = metaball(uv, mouse, radius);

    // Combine the field strengths
    float field = mb1 + mb2 + mb3;

    // Sharper edge for the metaballs
    float edgeSharpness = 0.003; // Lower this value for a sharper edge

    // Color the metaballs
    float colorIntensity = smoothstep(1.0 + edgeSharpness, 1.0 - edgeSharpness, field);

    // Set the color of the fragment
    fragColor = vec4(vec3(colorIntensity), 1.0);
}
