// GLSL translation of PICO-8 doodle by June
// https://mastodon.social/@wryl/110951502300150443

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float x = floor(fragCoord.x * 0.125);
    float y = floor(fragCoord.y * 0.125);

    int color = int((x*x*y*y+y) * 0.0625 + 2.0 * iTime) % 16 % 3;

    const vec3 palette[3] = vec3[3](
        vec3(0.00, 0.00, 0.00),
        vec3(0.50, 0.10, 0.30),
        vec3(0.05, 0.15, 0.30)
    );

    fragColor = vec4(palette[color], 1.0);
}
