void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizuotos pikselių koordinatės (nuo 0 iki 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Vaizdo apribojimas iki kvadrato
    float ar = iResolution.x / iResolution.y;
    uv.x *= ar;
    uv.x -= ar / 2. - 0.5;
    if (uv.x < 0. || uv.x > 1.) {
        fragColor = vec4(0);
        return;
    }

    // Su laiku besikeičianti pikselio spalva
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Išvestis į ekraną
    fragColor = vec4(col,1.0);
}
