#define PI 3.1415927
#define E.001

float estep(float threshold,float x,float e){
    return smoothstep(threshold+e,threshold-e,x);
}

float rand(float seed){
    float v=pow(abs(seed),6./7.);
    v*=sin(v)+1.;
    return fract(v);
}

vec3 col(float x){
    return vec3(
        .5*(sin(x*2.*PI)+1.),
        .5*(sin(x*2.*PI+2.*PI/3.)+1.),
        .5*(sin(x*2.*PI-2.*PI/3.)+1.)
    );
}

float saw(float x){
    return abs(mod(x+1.,2.)-1.);
}

float circle(vec2 uv,vec2 center,float size){
    uv-=center;
    return estep(size,length(uv),E);
}

vec3 cell(float size0, float color0, vec2 uv, float id, float t)
{
    float speed = .1 + rand(id + 4192.) * 3.;
    vec3 color = col(color0 + (rand(id + 5849.) - .5) * .1);
    float size = size0 * .1 + rand(id + 9582.) * size0 * .2;
    float px = size + saw(rand(id + 8457.) + speed * t) * (size0 - size * 2.);
    float py = size + saw(rand(id + 4628.) + speed * t)* (size0 - size * 2.);
    float thick = .65 + .2 * rand(id + 8371.);

    return color * (rand(id + 9471.) + .2) * vec3(circle(uv, vec2(px, py), size) - circle(uv, vec2(px, py), size * thick) * .75);
}



void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=(fragCoord.xy/iResolution.xy-.5);
    uv.x*=iResolution.x/iResolution.y;

    uv += iTime * .1 + 1.;

    float size = .01;

    vec3 c = vec3(0);

    for(int i = 0; i < 7; i++) {
        c = c * .75 + cell(size, iTime * .1, mod(uv, size), ceil(1728. + uv.x/size) * 7234. + ceil(9374. + uv.y/size) * 2348., iTime);

        size *= 1.5;
    }

    fragColor=vec4(c,1.);
}
