void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 size = vec2(4.0, 3.0);
    vec2 p = uv * size;

    vec2 st = fract(p);
    vec2 id = floor(vec2(p.x, size.y - p.y));
    float lod = id.x + size.x * id.y;

    vec3 col = textureLod(iChannel0, st, lod).rgb;

    fragColor = vec4(col,1.0);
}
