void mainImage( out vec4 O, vec2 u )
{
    vec3 p = iResolution,
         v = vec3((u+u-p.xy)/p.y/3., 1);

    v.zy *= mat2(cos(vec4(0, -11, 11, 0) - .4));

    for (float d, i; i++ < 1e2; d += 1. - .4 * O.r + .5 * p.y) 
        p = vec3(5,0,1) + .25 *v * d,
        O = texture(iChannel0, .1*p.xz);

    O *= 3. * O * O;
}

