void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float fac = 0.11;
    float current = 0.0;
    int lines = 9;

for(int i = 0; i < 9; i++)
{
  if(i < 4)
  {
    if(uv.x >= 0.25 && uv.y >= current && uv.y < current + fac)
    {
      if(i%2 == 0)
      {
      fragColor = vec4(0,0,.7,1);  
      }
      else
      {
      fragColor = vec4(1,1,1,1); 
      }
    }
  }
  else
  {
  if(uv.y >= current && uv.y < current + fac)
    {
    if(i%2 == 0)
      {
      fragColor = vec4(0,0,.7,1);  
      }
      else
      {
      fragColor = vec4(1,1,1,1); 
      }   
    }
  }
  current = current + fac;
}
if(uv.x <= .25 && uv.y <=.5)
{
 fragColor = vec4(0,0,.7,1);
}
if(uv.x <= .5/3.0 && uv.y <= .6)
