#define S(u,v,w) smoothstep(1.5/iResolution.x,0.,mod(u.x, line_step / v) - thickness * w ) + smoothstep(1.5/iResolution.x,0.,mod(u.y, line_step / v) - thickness * w )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 background_color = vec4(.8984375, .8984375, .91796875, 1.);

    vec4 line_color = vec4(.596078, .6784314, .9137255, 1.);

    float thickness = .001;
    float line_step = .25;


    vec2 uv = fragCoord/iResolution.x;

    float d = min(1., S(uv, 1., 2.) +  S(uv, 5., 1.) + S(uv, 50., .5));

    fragColor = mix(background_color, line_color, d);

}
