float noise(int id)
{
    float modVal = 13.0223f;
    return modf( ( cos(  (10.43f * mod(float(id), 3.41))) 
        + cos( iTime * 0.034994f * sin( mod(iTime * 0.1,1.930f) - 1.3239f * float(id))) ), modVal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = uv;

    int uvID = int(floor(uv.y*50.));
    uvID = int(mod(float(uvID), 9.));

    float noiseWeight = noise( int( mod(uv.x * mod(iTime*2.4012,4.923) - mod(iTime*4.,9.) + 5.,4.3)) );

    float freqH = 17.;
    float freqV = 8.18;
    uv.x += floor(uv2.x - mod(iTime*.2,3.) * (floor(uv2.y * freqV)/freqV) * freqH) / freqH;

    vec2 uvR = uv;
    vec2 uvG = uv;
    vec2 uvB = uv;
    uvR.x += noise(uvID) * (0.05 - ((sin(iTime)*.5)+.5) * 0.1) * noiseWeight;
    uvG.x += noise(uvID) * 0.15 * noiseWeight;
    uvB.x += noise(uvID) * -0.35 * noiseWeight;

    float r = texture(iChannel0, uvR).x;
    float g = texture(iChannel0, uvG).y;
    float b = texture(iChannel0, uvB).z;

    vec3 col = vec3(r,g,b);

    float scanlineThick = float(iResolution)/7.;
    float scanline = step( 1./float(iResolution), mod(floor(uv2.y * scanlineThick)/scanlineThick , 2./scanlineThick) );
    scanline *= 0.05;
    col -= scanline;

    // Output to screen
    fragColor = vec4(col,1.0);
}
