#define NOISE_RES 256.

float hash(vec2 n) {
    return fract(sin(dot(n, vec2(127.1, 311.7))) * 43758.5453);
}

float noise(vec2 n) {
    vec2 i = floor(n);
    vec2 f = fract(n);
    vec2 u = f*f*(3.0-2.0*f);
    return mix(mix(hash(i + vec2(0.0,0.0)), hash(i + vec2(1.0,0.0)), u.x), mix(hash(i + vec2(0.0,1.0)), hash(i + vec2(1.0,1.0)), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec3 color = vec3(0.0);
    float d;
    float speed = 0.1;
    for(int i=0; i<5; i++){
        vec2 newp = p;
        newp.x *= cos(iTime*speed + float(i)*7.1);
        newp.y *= sin(iTime*speed + float(i)*0.5);
        d = sin(length(newp)*5.0-iTime*2.0);
        color += vec3(d*noise(newp*2.0+iTime*0.1));
    }
    color = color/5.0;
    color = color * vec3(sin(iTime*0.1), cos(iTime*0.15), sin(iTime*0.4));
    color = color + vec3(pow(iMouse.x/iResolution.x,2.0));
    fragColor = vec4(color,1.0);
}
