const float Semitone  = 1.05946309436; //12 notes between an octave, octave is 2, so a semitone is 2^(1/12)

float KeyToFrequency(float n){
    return pow(Semitone,(n-8.))*440./48000.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy * pixelSize;

    if(uv.x > 1. - pixelSize.x){

      vec3 col = vec3(0.0);
      float nBands = 64.0;
      float i = floor(uv.y*nBands);
      float f = fract(uv.y*nBands);
      float band = KeyToFrequency(i);
      float s = texture( iChannel0, vec2(band,0.25) ).x;
      s += .2*s*texture( iChannel0, vec2(KeyToFrequency(i+12.),0.25) ).x;
      s *= s;

      col= vec3(s,4.*s*(1.-s),(1.-s))*s;

      vec3 col1 = vec3(0.0);
      col1 += .5*smoothstep(0.00,0.125,f);
      if (mod(i,12.)==2.){col1 = vec3(.1);}
      if (mod(i,12.)==4.){col1 = vec3(.1);}
      if (mod(i,12.)==7.){col1 = vec3(.1);}
      if (mod(i,12.)==9.){col1 = vec3(.1);}
      if (mod(i,12.)==11.){col1 = vec3(.1);}
      col += col1;

      col *= smoothstep(0.125,0.375,f);
      col = clamp(col, 0.0, 1.0);
	  fragColor = vec4(col,1.0);

    }else{
      fragColor = texture(iChannel1, uv + vec2(1,0) * pixelSize);
    }
}
