#define fmod(x,y) mod(floor(x),y)
vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

#define triwave_(p) abs(fract(.5+p/4.0)-.5)*2.
#define triwave1_(p) (abs(fract(p/8.0)-.5)-abs(fract(p/2.0)-.5)/2.)
float t1_(vec2 c, vec2 p){
    return triwave_((.5 - length( min(p=fract(p*sign(triwave1_(c*c.y))), 1.-p.yx) )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    vec2 uv = (fragCoord)/iResolution.y/2.0;
    uv.x += iTime/12.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy;

    vec2 t2 = vec2(0.);
    for(int k = 0; k < 9; k++){

        //uv.y += t1_(floor(uv),floor(uv));

        uv = (uv+t2)/1.5;
        t2 = triangle_wave(uv+.5);
        uv = t2-triangle_wave(uv.yx+1.)/1.5;
        //log-polar stuff
        //uv=vec2(log(length(uv/3.)), atan(uv.y/3., uv.x/3.))/3.;
        //if(uv.x>uv.y) uv.y += .5;
        col.x =
            max((t2.y+t2.x)/3.,col.x)
        ;
        col =
            abs(col-1.+col.x);
        //if(uv.y<.5) {uv=uv.yx;t2=t2.yx;}

    }
    fragColor = vec4(col,1.0);
}
