void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float radius = sqrt(uv.x*uv.x + uv.y*uv.y);
    float angle = cos(radius * 10.0 + iTime * 10.0) + 
    cos(+ iTime * 2.0 + cos(radius + iTime) * 10.0 + cos(iTime) + 12.0*atan(uv.y, uv.x));
    float a = atan(uv.y, uv.x);

    vec3 col = (angle > 0. ? 0.5 : 0.9) - (angle > 0. ? 0.2 : 0.5) * cos(a + vec3(0.0, 2.0, 4.0));


    fragColor = vec4(col-radius,1.0);
}
