void mainImage( out vec4 O, in vec2 C )
{
    vec4 o1=texture(iChannel0,C/iResolution.xy);
    O=o1;return;

    vec4 o2=texture(iChannel0,(C+vec2(8))/iResolution.xy);
    O=o1;
    O.yz=mix(O.yz,o2.yz,step(.4,sin(sin(iTime*2.)*8.)));

}

const float EPS=1e-4;
float map(vec3 p)
{
    return texture(iChannel0,p/80.).x* //random
            (dot(p.xy,p.xy)*.01+ //radius weight
            sin(iTime*.1)*.1);//animation
}
vec3 fog(vec3 p,vec3 o)
{
   p.z+=10.*iTime;
   return texture(iChannel0,p*.02).x*
           palette(o.z*.1);
}
vec3 minComponent(vec3 v)
{return min(v.xxx,min(v.y,v.z));}

void mainImage( out vec4 O, in vec2 C )
{

    vec3 dir=vec3((C-.5*iResolution.xy)/iResolution.x*3.,1),
    o=vec3(0.,0.,iTime),

    p=o+EPS*dir+vec3(EPS,2.*EPS,0),//fix:45Â° alias
    cn=step(vec3(0),dir),dif,m;

    float d,i;
    for(i=0.,O-=O;i<100.&&(d=map(floor(p)))<.42f;i++)
    {
        dif=cn-fract(p);
        dif/=dir;
        m=minComponent(dif);
        p+=dir*(m.x+EPS);
        O.xyz+=fog(p,o)*0.01;
    }if(i>99.) return;

    vec3 nml=-dir*step(dif,m)/(abs(dir)+EPS),po=o-p;
    O.xyz+=5910./(dot(po,po)+300.)*dot(nml,normalize(po))*
    vec3(palette(d*2.+floor(p*0.01+floor(iTime*4.)*0.04).z))*pow(1.-d,4.);
}

//Color Palette from iq
const float PI=3.1415;
vec3 palette(float t)
{
    t*=1.732;
    return vec3(.7)+vec3(.3)*cos(2.*PI*(vec3(.71,.31,.21)*t+vec3(.2,.4,.6)));
}
