/*
    Scene defined in 3d, but you see just a slice for some z value.
    Mouse.y changes z coordinate for slice.

    Debug:
        Lower left dot - last executed branch (case),
        Lower right dot - picked root (if any).

    In 3d: https://www.shadertoy.com/view/ctjcz3
*/

#define ORIGIN                    ( vec2(0) )
#define SCENE_SCALE               ( 2.5 )
#define UNIT                      ( 4.0*SCENE_SCALE / iResolution.y )          // Affects AA and thickness
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )

/* https://www.shadertoy.com/view/DdsGDj */
#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale - origin;
}

struct RAY
{
    vec3 position;
    vec3 direction;
};
struct HIT
{
    bool hit_something;
    float hit_dist;
    vec3 hit_point;
    vec3 hp_normal;
    int hitted_idx;
};
vec3 debug_color = vec3(0);
vec3 root_color = vec3(0);

// Ray-Object intersection routines.
HIT ray_unit_hemisphere_int(RAY ray) // sphere_pos always equals vec3(0), and sphere_r equals to 1.
{
    float half_b = dot(ray.position, ray.direction),
          c = dot(ray.position, ray.position) - 1.,
          discriminant = (half_b * half_b - c),
          sqrtDiscriminant = sqrt(discriminant),
          rootA = min(-half_b - sqrtDiscriminant, -half_b + sqrtDiscriminant);

    HIT hit;

    float rootB = -ray.position.y / ray.direction.y;
    vec3 hpB = ray.position + ray.direction * rootB;

    if (ray.position.y < 0. &&                         // Camera is below hemisphere?
        hpB.x*hpB.x + hpB.y*hpB.y + hpB.z*hpB.z <= 1.) // Outside of diks range?
    {      
        debug_color = vec3(1,0,0);

        hit.hit_something = true;
        hit.hit_dist = rootB;
        hit.hit_point = hpB;
        hit.hp_normal = vec3(0,-1,0);

        return hit;
    }

    hit.hit_point = ray.position + ray.direction * rootA;

    if (discriminant < 0. ||  // no real roots?
        rootA < 0. ||         // behind camera?
        hit.hit_point.y < 0.) // hp below hemisphere?
    { 
        debug_color = vec3(0,1,0);
        hit.hit_something = false;
        hit.hit_dist = 9e5;
        return hit; 
    }

    hit.hit_something = true;
    hit.hit_dist = rootA;
    hit.hp_normal = normalize(hit.hit_point);

    debug_color = vec3(0,0,1);
    root_color = rootA != rootB ?
        vec3(1,0,0) : vec3(0,0,1);

    return hit;
}

int func_idx = 0;
float implicit(float x, float y, float z) 
{   
    vec3 N = vec3(0,1,0);
    if (y >= 0.)
    {
        if (func_idx == 0)
            return x*x + z*z + y*y - 1.;
        else
            return (-N.y*y-1.) * float(x*x + z*z + y*y <= 1.);
    }
    else
        return 0.;

}
float implicitAA( in vec3 P )
{
    float coverage = 0., // You can cheat and set this variable to some negative value.
          th = 6.,        // thickness
          c = implicit(P.x, P.y, P.z) >= 0. ? 1. : -1.; // only sign

    vec2 ps = vec2(0.35*SCENE_SCALE/iResolution.y); // affects thickness and artifacts

    // Supersampling.
    for (float y = -th; y <= th; y += 1.)
        for (float x = -th; x <= th; x += 1.)
        {
            vec3 p = P + vec3(x, y, 0)*vec3(ps, 0);
            float n = implicit(p.x, p.y, p.z);

            /* 1. Filled shape *
            coverage += n >= 0.0 ? 0.0 : 1.0;
            /* 2. Shape contour */
            n = n >= 0.0 ? 1. : -1.;
            coverage += c == n ? 0. : 1.;
            /**/
        }

    // Convert coverage into intensity (.35 is arbitrary weight for AA).
    return smoothstep(0., th*th, coverage*0.35);
}

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = map_to_centered_ndc(SC, SCENE_SCALE, ORIGIN, false),
         MP = iMouse.xy == vec2(0) ? vec2(0) :  
              map_to_centered_ndc(iMouse.xy, SCENE_SCALE, ORIGIN, false);

    vec3 color = vec3(1);
         color = mix( color, vec3(0.8), SMAA(cellsSDF(NDC)) );
         //color = mix( color, vec3(0), SMAA(x_axisSDF(NDC)) );
         //color = mix( color, vec3(0), SMAA(y_axisSDF(NDC)) );

    // Z for slice.
    float //z_coord = 0.;
          z_coord = MP.y;

    // Draw 2d slice.
    vec3 point3d = vec3(NDC, z_coord);
    color = mix(color, vec3(1,0,0), SMAA(diskSDF(NDC, vec2(0), 0.02)));
    if (abs(point3d.x) < 1. && abs(point3d.y) <= 1. && abs(point3d.z) <= 1.)
    {       
        func_idx = 0; color = mix(color, vec3(0,0,1), implicitAA(point3d));
        func_idx = 1; color = mix(color, vec3(0,0,1), implicitAA(point3d));
    }

    // Ray position, direction.
    float T = iTime * 0.2;
    vec3 //r_orig = vec3(-0.5, -1.6, z_coord),
         //r_orig = vec3(sin(iTime)*2., -1.6, z_coord),
         r_orig = vec3(2. * cos(T), 2. * sin(cos(T*2.5)), z_coord),
         //r_orig = vec3(MP, z_coord),
         r_dir = normalize(vec3(0.5, -0.2, z_coord) - r_orig);
         //r_dir = normalize(vec3(MP, z_coord) - r_orig);

    color = mix(color, vec3(0.5), SMAA(diskSDF(NDC, r_orig.xy, 0.03)));  
    color = mix(color, vec3(0.7), SMAA(segmentSDF(NDC, r_orig.xy, (r_orig+r_dir*100.).xy, 0.)));
    color = mix(color, vec3(1,0,1), SMAA(arrowSDF(NDC, r_orig.xy, (r_orig+r_dir).xy, 0.)));

    // Intersection.
    RAY ray = RAY(r_orig, r_dir);
    HIT hit = ray_unit_hemisphere_int(ray);

    // Color for last executed branch.
    color = mix(color, debug_color, SMAA(diskSDF(NDC, vec2(-4, -2), 0.1)));

    if (hit.hit_something == true)
    {
        color = mix(color, root_color, SMAA(diskSDF(NDC, vec2(-3, -2), 0.1)));

        color = mix(color, vec3(0, 0.5, 0.5), SMAA(diskSDF(NDC, hit.hit_point.xy, 0.03)));
        color = mix(color, vec3(0, 0.5, 0.5), SMAA(arrowSDF(NDC, hit.hit_point.xy, hit.hit_point.xy + hit.hp_normal.xy, 0.)));
    }

    O = vec4(color,1.0);
}

float y_axisSDF(vec2 NDC) { return abs(NDC.x); }
float x_axisSDF(vec2 NDC) { return abs(NDC.y); }
float vertical_lineSDF(vec2 NDC, float x0) { return abs(NDC.x-x0); }
float horizontal_lineSDF(vec2 NDC, float y0) { return abs(NDC.y-y0); }
float cellsSDF(vec2 NDC)
{
    //NDC *= 2.; // Finer cell
    NDC = fract(NDC);
    NDC = fract(min(NDC, 1. - NDC));
    return
        min(vertical_lineSDF(NDC, 0.), horizontal_lineSDF(NDC, 0.));
}
float diskSDF(in vec2 NDC, in vec2 C, in float r) { return length(C - NDC)-r; }
float segmentSDF(in vec2 NDC, in vec2 B, in vec2 E, in float r)
{
    vec2 BP = NDC - B, BE = E - B;
    return length(BP - BE * clamp(dot(BP, BE) / dot(BE, BE), 0.0, 1.0)) - r;
}
float arrowSDF(
    in vec2 NDC,
    in vec2 B,
    in vec2 E,
    in float R)
{
    float len = length(B-E);    
    vec2 dir = normalize(E-B),
         //up = cross(vec3(dir, 0.), vec3(0, 0, -1)).xy,   
         up = vec2(-dir.y, dir.x),
         C = E - dir * 0.0475, //      U--
         U = C + up  * 0.030,  // B----C----E
         D = C - up  * 0.030;  //      D--

    vec3 BARY = inverse(mat3(
            U.x, U.y, 1.0,
            D.x, D.y, 1.0,
            E.x, E.y, 1.0)) * vec3(NDC.x, NDC.y, 1.0);

    float sign = 
        BARY.x >= 0.0 &&    
        BARY.y >= 0.0 && 
        BARY.z >= 0.0 ?  // inside triangle?
            -1. : 1.;

    float d = segmentSDF(NDC, U, D, 0.);
          d = min(d, segmentSDF(NDC, U, E, 0.));
          d = min(d, segmentSDF(NDC, D, E, 0.));
          d = 
              len < 0.0475 ? 
                  d :
                  min(d, segmentSDF(NDC, B, C, 0.));

    return sign*d-R;           
}
