/**/ //   282 chars 

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy, q,
         U =   ( 8.* ( u+u - R ) / R.y  + 2. ) 
             * mat2(cos( .1*iTime + vec4(0,33,11,0)))
             - 2.;
    float k = exp( log(2.618)* fract(iTime/4.) - .5 );
    mat2  P = mat2( vec2(1,-1.618) / 1.9 , 
                    vec2(1,  .618) / 1.176 );    
    q = vec2(k,1./k);
    U = P * ( U * q );
    O = R.yyyy/24.* ( length( ( U - round(U) ) * P / q ) - .3 );
}


/**  // 253 chars 

void mainImage(out vec4 O, vec2 u) {
    vec2  R = iResolution.xy, q;
    q.x = exp( log(2.618)* fract(iTime/4.) - .5 ), q.y = 1./q.x;
    mat2  P = mat2(.526,-.85, .85,.526);
    O = R.yyyy/24.* ( length( ( fract(P * ( (  ( 8.* ( u+u - R ) / R.y  + 2. ) 
                                              * mat2(cos( .1*iTime + vec4(0,33,11,0)))
                                              - 2.
                                            )* q 
                                          ) +.5
                                     ) -.5 
                              ) * P / q 
                            ) - .3 );
}


