void mainImage( out vec4 o, in vec2 f )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = f/iResolution.xy;
    float d = 10.0;
    vec3 m; 

    for(float i = 0.;i<20.;i+=1.){

        vec2 r = 0.5+ vec2(cos(i+iTime*i*0.2)/(i), sin(i+iTime*i*0.2)/i)-uv;
        if( length(r)<d ){
            float ang = (3.14/2.0 +atan(r.y,r.x))/3.14;
            d=length(r);
            float dd = floor(d*50.)/50.;
            o = 0.5+cos(vec4(ang + dd*sin(iTime)*34.)*6.2);
        }


    }

}
