void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float progress = mod(iTime*0.5, 1.0);
    float progressInv = 1.0 - progress;

    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;
    //uv.x *= aspectRatio;
    //uv -= 0.5;

    vec2 focus = iMouse.xy / iResolution.xy;
    if (length(focus) < 0.01) {
        focus = vec2(0.5, 0.5);
    }
    //focus.x *= aspectRatio;
    //focus -= 0.5;

    float circleSize = progress*0.2;

    vec4 color = vec4(0.0, 0.0, 0.0, 1.0);
    vec2 norm = uv - focus;
    float distMap = (circleSize - length(norm)) / circleSize;
    if(distMap > 0.0) {
        vec2 dUv = uv + norm * pow(length(norm) / circleSize, 6.0) * progressInv;
        color = progress * pow(distMap, 0.5) * texture(iChannel0, dUv, progressInv*6.0); // fake sphere
    }

    fragColor = color;
}
