const float WATER_SCALE = 0.04;
const float WATER_SPEED = 0.02;

float waterHeight(vec2 At)
{
    At /= iResolution.xy;
    float phase = cos(iTime * 160.0 * WATER_SPEED) * 0.2 + 0.8;
    float x0 = texture(iChannel0, At * WATER_SCALE + iTime * WATER_SPEED).x;
    float x1 = texture(iChannel1, At * WATER_SCALE + iTime * WATER_SPEED).x;
    return x0 + phase * (x1 - x0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float waterDepth = 64.0; 

    float x = waterHeight(fragCoord);
    vec3 x3 = vec3(fragCoord.x, x * 4.0, fragCoord.y);
    float xx = waterHeight(fragCoord + vec2(1.0, 0.0));
    vec3 xx3 = vec3(fragCoord.x + 0.2, xx * 4.0, fragCoord.y);
    float xy = waterHeight(fragCoord + vec2(0.0, 1.0));
    vec3 xy3 = vec3(fragCoord.x, xy * 4.0, fragCoord.y + 0.2);

    vec3 normal = normalize(cross(xx3 - x3, xy3 - x3));

    vec3 RayDir = vec3(0, -1.0, 0);
    RayDir = refract(RayDir, normal, 1.33);

    fragCoord.x += RayDir.x * (x + waterDepth);
    fragCoord.y += RayDir.z * (x + waterDepth);

    vec4 outColor = texture(iChannel2, fragCoord.xy / iResolution.xy);

    fragColor = outColor;
}
