#define PI 3.14159265359

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv-0.5;
    uv.x *= iResolution.x/iResolution.y;

    // final color for the pixel
    vec3 hsv = vec3(0.0);

    // radius, angle, and time
    float r = length(uv)*2.0;
    float a = atan(uv.y,uv.x); // -PI ~ PI (counterclockwise)
    float t = fract(iTime/(2.0*PI))*2.0*PI; // Repeats over 0 ~ 2PI for continuous loop

    float size = 0.45 + 0.15*sin(1.0*t); // 0.3 ~ 0.6
    float core = size + 0.2*sin(a+2.0*t)*cos(5.0*a+t); // 0.1 ~ 0.8

    hsv[0] = size;
    hsv[1] = r/core;
    hsv[2] = 1.0-smoothstep(core,core+0.02,r);

    vec3 color = hsb2rgb(hsv);
    fragColor = vec4(color, 1.0);
}
