vec4 circle(vec2 uv, float r){
float sm = 0.05;
float d = length(uv-0.05);
vec4 color = vec4(length(uv), 0.198,0.945, 1.0);
color.a = smoothstep(r, r + sm, d);
return  color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= (0.9 + 0.4*abs(sin(iTime)))* iResolution.x/iResolution.y;
    uv.y -= -0.3 + 0.5*abs(sin(iTime));
    vec4 color = vec4(0.062,0.998,0.945,1.0);
    color.y = smoothstep(length(uv), 0.2, 0.19);

    vec4 blick = circle(uv, 0.01);
    color = mix(blick, color,  blick.a);

    fragColor = vec4(color);
}
