void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float filter_width = sqrt(2.0);
    const float upscale_factor = 7.37;
    // In a real application, this constant should be precalculated
    // and passed in as a uniform.
    const float d = upscale_factor / filter_width;

    float t1 = iTime * 0.031;
	vec2 uv = (fragCoord.xy / iResolution.x);
    uv = mat2(sin(t1), cos(t1), -cos(t1), sin(t1)) * uv;
    uv *= iResolution.x / upscale_factor;

    //----------


    if (fract(iTime / 4.0) > 0.5) {
        // We reshape the fractional component of the texture coordinate
        // so that it looks like this as it goes across a source pixel -

        //       /
        //   ----    <-- 0.5
        //  /

        // where the slopes of the end segments are equal to
        // (upscale_factor / filter_width)

        vec2 uf = fract(uv) * d;
        uf -= clamp(uf - 0.5, 0.0, d - 1.0);

        // And then we add our reshaped fractional component back to the
        // integer component.
        uv = floor(uv) + uf;
    }

    fragColor = texture(iChannel0,uv / iChannelResolution[0].x);
}
