void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Speed = 1.5;
    float Time = sin(iTime * Speed) * 0.5 + 0.6;

    vec2 Coords = fragCoord.xy / iResolution.xy;

    vec4 Color = vec4(Coords.x, Coords.y, 2.0, 0.0);

    Color = Color * Time;

    fragColor = Color;
}
