/////////////////////////////////////////////////////////////////////   //
     //                                                                 //
     // controls:                                                       //
     //                                                                 //
     // â€‹ drag the end and control points to change the curve           //
     // â€‹ space : toggle guide lines                                    //
     // â€‹ enter : reset end and control points                          //
     // â€‹ 't'   : toggle tutorial vs ajs15822 / iq distance function    //
     // â€‹ 'r'   : toggle "ravg" distance function                       //
     //                                                                 //
     /////////////////////////////////////////////////////////////////////
     //                                                                 //
     // links:                                                          //
     //                                                                 //
     // â€‹ BigWings's Video https://www.youtube.com/watch?v=a4zMX6dDVXI  //
     // â€‹ https://www.shadertoy.com/view/ltXSDB Adam Simmons            //
     // â€‹ https://www.shadertoy.com/view/MslSDN Sebastien Durand        //
     // â€‹ https://www.shadertoy.com/view/XsX3zf Tay Holliday            //
     // â€‹ https://www.shadertoy.com/view/3dKSDc iq's X                  //
     // â€‹ https://hhoppe.com/ravg.pdf           Nehab & Hoppe (2008)    //
     //                                                                 //
     /////////////////////////////////////////////////////////////////////
     //                                                                 //
     // notes:                                                          //
     //                                                                 //
     // â€‹ the tutorial version with guide lines uses fewer points to    //
     //   showcase the technique.                                       //
     // â€‹ the distance field for "ravg" can be pretty wild.             //
     // â€‹ lists with less than 3 items just look kinda weird to me...   //
     //                                                                 //
     /////////////////////////////////////////////////////////////////////

vec3 bezierDemo( vec2 uv, vec2 a, vec2 c, vec2 z, bool showGuideLines, bool useTutorial, bool useRavg ) {

    float r = .033;
    float s = r * .77;
    float l = r * .077;

    // use viewer points to show the magic!
    int steps = showGuideLines ? 6 : 66;

    ////////////////////////////////////

    vec2 bd = useTutorial
        ? bezierDistance( uv, a, c, z, steps )
        : abs( sdBezier( uv, a, c, z ) );

    if ( useRavg ) {
        bd = ravgBezier( uv, a, c, z );
    }

    ////////////////////////////////////

    float distanceToCurve = bd.x;
    float distanceAlongCurve = bd.y;

    ///////////////////

    // background shows distance to curve and length along path
    vec3 color = colorF( distanceToCurve * 6.6 ) * max( distanceAlongCurve, .33) * .33;
    //color = vec3( distanceAlongCurve, min( distanceToCurve, distanceAlongCurve ), d * 3.3 ) * .33;

    // show the curve
    color += colorF( distanceAlongCurve ) *  shape( distanceToCurve, l );

    ////////////////////////////////////
    // show the end and control points

    float d1 = circleDistance( uv, a ); // <><
    float d2 = circleDistance( uv, z ); //     <><
    float d3 = sdRoundedX( uv - c, 1.77 * r , -.02); // <><
    float mino = min( d1, min( d2, d3 ) ); // <>< 

    if ( mino < r ) {
        vec3 colorC = colorF( uv.x + uv.y * 2. );
        color = mix( INVERT(color), colorC, INVERT(mino) ); // ><>
    }

    ////////////////////////////////////
    // show guide lines

    if ( showGuideLines ) {
        float t = HALBER( sin( iTime ) );
        vec2 ca = mix( a, c, t );
        vec2 cb = mix( c, z, t );
        vec2 ct = mix( ca, cb, t );

        color += V101.zzy * circle( uv, ca, r );
        color += V101.yzz * circle( uv, cb, r );
        color += V101.zyz * circle( uv, ct, r );

        color += line( uv, a, c, l );
        color += line( uv, z, c, l );
        color += line( uv, ca, cb, l );
    }

    ////////////////////////////////////

    return color;
}

vec3 demo( vec2 uv, vec2 ms ) {
    bool showGuideLines = !keyToggle( 32, iChannel0 );
    bool useTutorial = !keyToggle( 84, iChannel0 );
    bool useRavg = keyToggle( 82, iChannel0 );

    vec2 c0 = ball( 0, iChannel1 ).xy;
    vec2 c1 = ball( 1, iChannel1 ).xy;
    vec2 c2 = ball( 2, iChannel1 ).xy;

    return bezierDemo( uv, c0, c1, c2, showGuideLines, useTutorial, useRavg );
}

////////////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    INIT_SHADER_INPUTS( fragCoord );
    fragColor = vec4( demo( SHADER_INPUTS.uv, SHADER_INPUTS.ms ), EINS );
}

////////////////////////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////////////////////////

const float ZED  = .0;
const float EINS = 1.;
const float HALB = .5;
const float TAU  = 99733. / 15873.;

const vec3 V101 = vec3( -EINS, ZED, +EINS );

////////////////////////////////////////////////////////////////////////////////////

#define HALBER(v) (v * HALB + HALB)
#define INVERT(v) (EINS-v)

////////////////////////////////////////////////////////////////////////////////////

vec4 gimme( in ivec2 st, sampler2D  sampler) {
    return texelFetch( sampler, ivec2( st ), 0 );
}

vec4 gimme( in vec2 st, sampler2D  sampler) {
    return gimme( ivec2( st ), sampler );
}

bool keyDown( int key, sampler2D keyboardChannel ) {
    return ZED < gimme( ivec2(key,ZED), keyboardChannel ).x;
}

bool keyToggle( int key, sampler2D keyboardChannel ) {
    return ZED < gimme( ivec2(key,2), keyboardChannel ).x;
}

////////////////////////////////////////////////////////////////////////////////////

const int BALL_COUNT = 3; // just need 3 for this...

bool noBalls( in ivec2 st ) {
    return 0 != st.y && st.x >= BALL_COUNT;
}

vec4 ball( in int i, sampler2D sampler ) { 
     return gimme( ivec2( i, 0), sampler );
}

////////////////////////////////////////////////////////////////////////////////////
// make this magic a bit easier to deal with

const int MOUSE_UP = -1;
const int MOUSE_DOWN = 1;
const int MOUSE_CLICKED = 2;

struct ShaderInputs { vec3 iResolution;float iTime;float iTimeDelta;float iFrameRate;int iFrame;float iChannelTime[4];vec3 iChannelResolution[4];vec4 iMouse;vec4 iDate;float iSampleRate;vec2 fragCoord;vec2 uv;vec2 ms;vec2 trigTime;int mouseStatus;};
ShaderInputs SHADER_INPUTS;

#define INIT_SHADER_INPUTS(fragCoord) \
{\
    vec2 uv = pixelToUv( fragCoord, iResolution.xy ); \
    vec2 trigTime = trig( iTime ); \
    int mouseStatus = MOUSE_UP + int( 2. * step( ZED, iMouse.z ) + step( ZED, iMouse.w ) ); \
    vec2 ms = (MOUSE_UP == mouseStatus) ? trigTime : pixelToUv( iMouse.xy, iResolution.xy ); \
    SHADER_INPUTS = ShaderInputs( iResolution, iTime, iTimeDelta, iFrameRate, iFrame, iChannelTime, iChannelResolution, iMouse, iDate, iSampleRate, fragCoord, uv, ms, trigTime, mouseStatus ); \
}

////////////////////////////////////////////////////////////////////////////////////

vec2 trig( float angle ) {
    return vec2( cos(angle), sin(angle) );
}

vec2 pixelToUv( vec2 st, vec2 resolution ) {
    return ( st * 2. - resolution.xy ) / resolution.y;
}

vec2 pixelToUv( vec2 st ) {
    return pixelToUv( st, SHADER_INPUTS.iResolution.xy );
}

////////////////////////////////////////////////////////////////////////////////////

vec3 colorF( float f ) {
    return vec3( f, HALBER( trig( 6.6 * f ) ) );
}

////////////////////////////////////////////////////////////////////////////////////

float shape( float d, float radius ) {
    return smoothstep( fwidth( d ), ZED, d - radius );
}

float circleDistance( vec2 p, vec2 center ) {
    return distance( p, center );
}

float circle( vec2 p, vec2 center, float radius ) {
    return shape( circleDistance( p, center ), radius );
}

vec2 lineDistance2( vec2 p, vec2 a, vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return vec2( length( pa - ba*h ), h );
}

float lineDistance( vec2 p, vec2 a, vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float line( vec2 p, vec2 a, vec2 b, float thickness ) {
    return shape( lineDistance( p, a, b ), thickness );
}

////////////////////////////////////////////////////////////////////////////////////
// https://www.youtube.com/watch?v=a4zMX6dDVXI&ab_channel=TheArtofCode

vec2 bezierPoint( in vec2 c1, in vec2 cc, in vec2 c2, float t ) {
    vec2 ca = mix( c1, cc, t );
    vec2 cb = mix( cc, c2, t );
    return mix( ca, cb, t );
}

vec2 bezierDistance( in vec2 p, in vec2 c1, in vec2 cc, in vec2 c2, in int steps ) {
    float d = 3e3;
    float t = ZED;
    vec2 last = c1;

    for ( int i = 1 ; i <= steps ; i++ ) {
        float f = float( i ) / float( steps );
        vec2 current = bezierPoint( c1, cc, c2, f );
        float dt = lineDistance( p, current, last );

        //d = min( d, dt );
        if ( dt < d ) {
            d = dt;
            t = f;
        }

        last = current;
    }

    return vec2( d, t );
}

////////////////////////////////////////////////////////////////////////////////////
// https://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf
// via https://www.shadertoy.com/view/ldj3Wh, MslSDN and XsX3zf

float det(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; } // aka 2d cross product
// Find vector ð‘£ð‘– given pixel ð‘=(0,0) and BÃ©zier points ð‘0,ð‘1,ð‘2.
vec4 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {
    float a=det(b0,b2), b=2.*det(b1,b0), d=2.*det(b2,b1); // ð›¼,ð›½,ð›¿(ð‘)
    float f=b*d-a*a; // ð‘“(ð‘)
    vec2 d21=b2-b1, d10=b1-b0, d20=b2-b0;
    vec2 gf=2.*(b*d21+d*d10+a*d20);
    gf=vec2(gf.y,-gf.x); // âˆ‡ð‘“(ð‘)
    vec2 pp=-f*gf/dot(gf,gf); // ð‘ â€²
    vec2 d0p=b0-pp; // ð‘â€² to origin
    float ap=det(d0p,d20), bp=2.*det(d10,d0p); // ð›¼,ð›½(ð‘ â€²)
    // (note that 2*ap+bp+dp=2*a+b+d=4*area(b0,b1,b2))
    float t=clamp((ap+bp)/(2.*a+b+d), .0,1.); // ð‘¡Ì…
    vec2 og = mix(mix(b0,b1,t),mix(b1,b2,t),t); // ð‘£ð‘– = ð‘(ð‘¡Ì… ) 
    return vec4(length(og),t,og);
}

float area( vec2 a, vec2 b, vec2 c ) {
    return a.x * (b.y - c.y) + b.x * (c.y - a.y) + c.x * (a.y - b.y);
}

vec2 ravgBezier(vec2 p, vec2 b0, vec2 b1, vec2 b2) {
    return 
        ( abs( det( b1-b0, b2-b0 ) ) < .029)
        ? lineDistance2( p, b0, b2 )
        : get_distance_vector( b0-p, b1-p, b2-p ).xy;
}

////////////////////////////////////////////////////////////////////////////////////
// https://www.shadertoy.com/view/ltXSDB

// Test if point p crosses line (a, b), returns sign of result
float testCross(vec2 a, vec2 b, vec2 p) {
    return sign((b.y-a.y) * (p.x-a.x) - (b.x-a.x) * (p.y-a.y));
}

// Determine which side we're on (using barycentric parameterization)
float signBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{ 
    vec2 a = C - A, b = B - A, c = p - A;
    vec2 bary = vec2(c.x*b.y-b.x*c.y,a.x*c.y-c.x*a.y) / (a.x*b.y-b.x*a.y);
    vec2 d = vec2(bary.y * 0.5, 0.0) + 1.0 - bary.x - bary.y;
    return mix(sign(d.x * d.x - d.y), mix(-1.0, 1.0, 
        step(testCross(A, B, p) * testCross(B, C, p), 0.0)),
        step((d.x - d.y), 0.0)) * testCross(A, C, B);
}

// Solve cubic equation for roots
vec3 solveCubic(float a, float b, float c)
{
    float p = b - a*a / 3.0, p3 = p*p*p;
    float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
    float d = q*q + 4.0*p3 / 27.0;
    float offset = -a / 3.0;
    if(d >= 0.0) { 
        float z = sqrt(d);
        vec2 x = (vec2(z, -z) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        return vec3(offset + uv.x + uv.y);
    }
    float v = acos(-sqrt(-27.0 / p3) * q / 2.0) / 3.0;
    float m = cos(v), n = sin(v)*1.732050808;
    return vec3(m + m, -n - m, n - m) * sqrt(-p / 3.0) + offset;
}

// Find the signed distance from a point to a bezier curve
vec2 sdBezier(vec2 p, vec2 A, vec2 B, vec2 C )
{    
    B = mix(B + vec2(1e-4), B, abs(sign(B * 2.0 - A - C)));
    vec2 a = B - A, b = A - B * 2.0 + C, c = a * 2.0, d = A - p;
    vec3 k = vec3(3.*dot(a,b),2.*dot(a,a)+dot(d,b),dot(d,a)) / dot(b,b);      
    vec3 t = clamp(solveCubic(k.x, k.y, k.z), 0.0, 1.0);
    vec2 pos = A + (c + b*t.x)*t.x;
    float dis = length(pos - p);
    pos = A + (c + b*t.y)*t.y;
    dis = min(dis, length(pos - p));
    pos = A + (c + b*t.z)*t.z;
    dis = min(dis, length(pos - p));
    return vec2( dis * signBezier(A, B, C, p), t );
}

////////////////////////////////////////////////////////////////////////////////////

// iq: https://www.shadertoy.com/view/3dKSDc
float sdRoundedX( in vec2 p, in float w, in float r ) {
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

////////////////////////////////////////////////////////////////////////////////////

// simple interactive balls
// buffer reset key is ENTER

bool isDragging( vec4 ball ) {
    return ball.w > HALB;
}

bool alreadyDragging( sampler2D sampler ) {
    for ( int i = 0 ; i < BALL_COUNT ; i++ ) {
        vec4 ball = ball( i, sampler );
        if ( isDragging( ball ) ) return true;
    }
    return false;
}

float ballToFloat( int n ) {
    return float( n ) / float( BALL_COUNT ); 
}

bool reset() {
    return keyDown( 13, iChannel1 ) || iFrame < 33;
}

vec4 init( in ivec2 st ) {
    if ( noBalls( st ) ) return V101.yyyy;
    float x = 1.5 * sign( float( st.x % 2 ) - .5 );
    float y = 0.8 * sign( float( st.x / 2 ) - .5 );
    return vec4( x, y, .066, ZED );
}

vec4 interact( in ivec2 st, inout vec4 ball ) {
    if ( noBalls( st ) )  return ball;

    vec4 mouse = SHADER_INPUTS.iMouse;
    vec2 ms = SHADER_INPUTS.ms;
    int status = SHADER_INPUTS.mouseStatus;  

    if ( status == MOUSE_UP ) {
        ball.w = ZED;
        return ball;
    }   

    if ( isDragging( ball ) ) {
        ball.xy = ms.xy;
        return ball;
    }

    float d = distance( ms.xy, ball.xy );
    if ( d > ball.z ) return ball;

    if ( alreadyDragging(iChannel0) ) { 
        return ball; 
    }

    ball.xy = ms.xy;
    ball.w = EINS;

    return ball;
}

void mainImage( out vec4 val, in vec2 st ) {
    INIT_SHADER_INPUTS( st );

    ivec2 ij = ivec2( st );
    val = reset() ? init( ij ) : gimme( ij, iChannel0 );   
    val = interact( ij, val );
}
