#define NUM_TICKS 50.0
#define DIVIDE 10.0
#define PI 3.14159
#define WIDTH .001
#define HEIGHT .5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float tickWidth = 1.0;
    float tickNum = ceil(uv.x * NUM_TICKS);

    float remainder = mod(tickNum, DIVIDE);

    if (remainder == 0.0)
    {
        tickWidth = 2.0;
    }

    float x = abs ( tickWidth *
        sin (
            uv.x * (NUM_TICKS * PI)
        )
    );

    x = step(x, 1.0 - WIDTH * (iResolution.x/NUM_TICKS));

    vec3 color = vec3(x);

    if (uv.y > 1.0 - HEIGHT)
    {
        fragColor = vec4(color,1.0);
    } else if (remainder == 0.0)
    {
        fragColor = vec4(color,1.0);
    }
}
