#define PI 3.14159265
#define TWO_PI 6.28318530

//uniform float iTime;
//uniform float iResolution;
uniform float iAmplitude;
uniform float iFrequency[3];
uniform sampler2D screenTexture;


float rand_a(float x)
{
    return fract(sin(x) *  43758.5453) * iTime * 0.05;
}

float rand_b(vec2 st)
{
    return fract(sin(dot((st), vec2(12.9898, 78.233))) * 43758.5453);
}

float sampleMusic()
{
    return texture(iChannel0, vec2(0.25,0.0)).x * 0.02;
}    

vec3 hsv2rgb (in vec3 hsv) //color change
{
    vec3 rgb = clamp(abs(mod(hsv.x * 3.0 + vec3(4.0, 2.0, 0.0), 8.0) - 4.0) - 1.0, 0.0, 1.0)
               * cos(TWO_PI * (hsv.x + vec3(0.0, 1.0, 2.0)));

    return hsv.z * mix(vec3(1.0), rgb, hsv.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * 2.0;
    float aspect = iResolution.x / iResolution.y;
    uv *= aspect * 4.0;

    float pulse = sampleMusic(); 
    float posX = rand_a(floor(uv.x));
    float posY = rand_a(floor(uv.y));
    float time = cos(floor(posX / posY) * iTime);

    float randomColor = rand_b(floor(uv)) * (0.5 + 0.5 * cos(iTime * 0.5)) * 2.0;

    //(Grid Edge)
    vec2 black = smoothstep(1.0, 0.8, cos(uv * TWO_PI));

    vec3 color = hsv2rgb(vec3(randomColor,1.0,1.0));

    //(Inside Block Blur)
    color *= (black.x * black.y) * smoothstep(1.0, 0.25, length(fract(uv) - 0.5));
    color *= 0.5 + 0.5 * fract(sin((posX + posY) * pulse));

    float id = 0.5 + 0.5 * cos(iTime + sin(dot(floor(uv), fract(vec2(posY,posX)))));
    vec3 coord = 0.5 + 0.5 * cos(iTime + 4.0 * id + vec3(2.0, 6.0, 4.0));
    vec2 blur = smoothstep(0.0, 0.2, id * (0.5 + 0.5 * cos(TWO_PI * uv) * 0.1));
    blur *= smoothstep(1.0, 0.8, cos(uv * TWO_PI));

    vec3 pattern = vec3((blur.x * blur.y) * coord);

    color = mix(color,pattern, 0.15) * 1.5;

    fragColor = vec4(color, 1.0);
}
