void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float width = iResolution.x;
    float height = iResolution.y;

    vec2 translate = vec2(-1.781, 0.025);
    float zoom = pow(2., iTime) - 0.5;

    vec3 col1 = vec3(0.955, 0.879, 0.784);
    vec3 col2 = vec3(0.275, 0.104, 0.063);

    int passes = 1;
    int xoffset;
    int yoffset;
    float fac = 0.;

    float x = 0.0;
    float y = 0.0;

    for (xoffset = 0; xoffset < passes; xoffset++) {

        for (yoffset = 0; yoffset < passes; yoffset++) {

            float _x = uv.x / (zoom*height/3000.) + translate.x + float(xoffset)/(zoom*1000.);
            float _y = uv.y / (zoom*width/3000.) + translate.y + float(yoffset)/(zoom*1000.);

            int iteration = 0;
            int max_iteration = int(100.*log2(zoom))+150;
            while ( (x*x + y*y < 4.) && (iteration < max_iteration) )
            {
                float xtemp = x*x - y*y + _x;
                y = -abs(2.*x*y) + _y;
                x = xtemp;
                iteration++;
            }

            fac += float(iteration)/float(max_iteration);

        }
    }

    fac /= float(passes);

    // vec3 mixedCol = mix(col1, col2, pow(float(iteration)/float(max_iteration), 0.5));
    // if (iteration == max_iteration) {mixedCol = vec3(0.);}
    fragColor = vec4(vec3(fac), 1.);
}
