void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 m = (.5 * iResolution.xy - iMouse.xy) / iResolution.x * 6.28;
    vec3 e = vec3(cos(m.x) * cos(m.y), sin(m.x) * cos(m.y), sin(m.y));
    vec3 u = normalize(vec3(e.y, -e.x, 0));
    vec3 v = cross(e, u);
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.x;
    vec3 d = e + uv.x * u + uv.y * v;
    vec3 a = (e + .1) / d;
    vec3 b = (e - .1) / d;
    float mi = max(max(min(a.x, b.x), min(a.y, b.y)), min(a.z, b.z));
    float ma = min(min(max(a.x, b.x), max(a.y, b.y)), max(a.z, b.z));
    float t = mi < ma ? mi : 10.;
    vec3 i = e - d * t;
    vec3 c = (step(-.099, i) + step(.099, i) - 1.) * d;
	fragColor = vec4(c, 1.);
}
