void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.*((fragCoord/iResolution.xy)-0.5);
    uv.x *= iResolution.x/iResolution.y;
    float offset = (cos(iTime/4.)+1.)/4.;
    vec2 c = mix(1.4*vec2(uv.x-offset, uv.y), vec2(-0.6, 0.6), (-cos(iTime/4.)+1.)/2.);
    vec2 z = mix(vec2(0), 1.4*vec2(uv.x-offset, uv.y), (-cos(iTime/4.)+1.)/2.);
    vec3 col = vec3(0);
    for (int i = 0; i < 128; i++) {
        z = vec2(z.x*z.x - z.y*z.y, 2.*(z.x*z.y)) + c;
        float p = mod((float(i) - log(log(dot(z, z)) / log(4.)) / log(2.))/6., 6.);
        if (length(z) > 4.) { col = vec3(vec3(abs(p-3.)-1., -abs(p-2.)+2., -abs(p-4.)+2.)); break; }
        else { col = vec3(0); }
    }
    fragColor = vec4(col,1.0);
}
