float rectSDF(vec2 st, vec2 s) {
    st = st * 2.0 - 1.0;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}

float fill(float x, float size) {
    return 1.0 - step(size, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(1.0);

    float MX = iMouse.x / iResolution.x;
    float MY = iMouse.y / iResolution.y;

    vec2 s = vec2(0.15, 1.0);

    float rect = fill(rectSDF(uv, s), 1.0);
    float mask = fill(rectSDF((uv + vec2(0.06, 0.0)) - uv.y * 0.12, s), 1.0);

    color *= rect * vec3((ceil(uv.y * 17.0) / 17.0) - 0.1);
    color = mix(color, rect * vec3(0.4), rect - (mask * (1.0 - MX)));

    fragColor = vec4(color, 1.0);
}
