void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Texture uvs
    vec2 u = fragCoord/iResolution.y*.2+iTime*.1;
    //Texture resolution
    vec2 r = iChannelResolution[0].xy;
    //Tau * texture resolution
    vec2 s = 6.283185 * r;

    //Sample with sin interpolation
    fragColor = texture(iChannel0, u + sin(u*s)/s*0.5).rrrr;

    //Linear interpolation
    //fragColor = texture(iChannel0, u).rrrr;

    //Cubic interpolation
    //fragColor = texture(iChannel0, u+(smoothstep(0.,1.,fract(u*r+.5))-fract(u*r+.5))/r).rrrr;
}
