void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float pi = 3.14159;
    vec3 col = vec3(0.0,0.0,0.0);
    vec4 img = texture(iChannel0, uv);
    //float comp = /64.0


    // grayscale = 0.299 ∙ Red + 0.587 ∙ Green + 0.114 ∙ Blue
    float grays = 0.299 * img.x + 0.587 * img.y + 0.114 * img.z;

    float period;
    float numDis = 10.0;
    float width = 10.0;
    period= width/(1.0 - floor(grays*numDis)/numDis);
    period = 5.0;

    float func;
    float theta = 30.0;
    vec2 po = vec2(0, 0);
    vec2 dir = vec2(cos(theta*pi/180.0), sin(theta*pi/180.0));
    func = dot((fragCoord-po), dir);

    // func = func / T
    func = func / period;
    func = sin(func*pi);//func - floor(func);



    col = vec3(ceil(grays - func));

    // Output to screen
    fragColor = vec4(col,1.0);//vec4(col,1.0); img;
}
