// Möbius transform:
// Complex numbers
//     a z + b
//     -------
//     c z + d

#define real(z) (z).x
#define imag(z) (z).y

vec2 cmul(vec2 a, vec2 b)
{
    return
        vec2
        (
            a.x*b.x-a.y*b.y,
            a.x*b.y+a.y*b.x
        );
}

vec2 cdiv(vec2 a, vec2 b)
{
    return
        vec2
        (
            a.x*b.x+a.y*b.y,
            a.y*b.x-a.x*b.y
        )
        /
        dot(b,b);
}

struct cmat2
{
    vec2 a, b, c, d;
};

// / a b \   / A B \   / aA+bC aB+bD \
// |     | * |     | = |             |
// \ c d /   \ C D /   \ cA+dC cB+dD /
cmat2 cmul(cmat2 p, cmat2 q)
{
    return
        cmat2
        (
            p.a * q.a + p.b * q.c, p.a * q.b + p.b * q.d,
            p.c * q.a + p.d * q.c, p.c * q.b + p.d * q.d
        );
}

// https://en.wikipedia.org/wiki/M%C3%B6bius_transformation#Formula_for_the_inverse_transformation
cmat2 inverse_moebius(cmat2 m)
{
    return
        cmat2
        (
            m.d,-m.b,
            -m.c,m.a
        );
}

// https://en.wikipedia.org/wiki/M%C3%B6bius_transformation#Specifying_a_transformation_by_three_points
cmat2 moebius_by_triangle(vec2 z1, vec2 z2, vec2 z3)
{
    return // transform from three points to 0,1,infty
        cmat2
        (
            // Thanks to mla for debugging missing complex multiplication here!!!
            z2-z3,-cmul(z1,z2-z3),
            z2-z1,-cmul(z3,z2-z1)
        );
}

// https://en.wikipedia.org/wiki/M%C3%B6bius_transformation#Specifying_a_transformation_by_three_points
cmat2 moebius_by_triangles(vec2 p0, vec2 q0, vec2 p1, vec2 q1, vec2 p2, vec2 q2)
{
    // map triangle to 0,1,infinity then 0,1,infinity to other triangle
    if(false) // which direction?
    {
    return
        cmul
        (
            moebius_by_triangle(p0,p1,p2),
            inverse_moebius(moebius_by_triangle(q0,q1,q2))
        );
    }
    return
        cmul
        (
            inverse_moebius(moebius_by_triangle(p0,p1,p2)),
            moebius_by_triangle(q0,q1,q2)
        );
}

// Thanks to mla for the (in retrospect) obvious concise implementation:
// mla: "How about just defining complex multiplication
//       and division as cmul and cdiv say,
//       and doing return cdiv(cmul(z,a)+b,cmul(z,c)+d) ?"
vec2 apply_moebius(vec2 a, vec2 b, vec2 c, vec2 d, vec2 z)
{
    return cdiv(cmul(z,a)+b,cmul(z,c)+d);
}

vec2 apply_moebius(cmat2 m, vec2 z)
{
    return cdiv(cmul(z,m.a)+m.b,cmul(z,m.c)+m.d);
}

#define pi 3.1415926

float checker(vec2 p)
{
    float d = 10.0/iResolution.y;
    if(true)
    {
        p = sin(2.0*pi*p);
        return smoothstep(-d,+d, p.x*p.y);
    }
    p = fract(p)-0.5;
    return smoothstep(-d,+d,max(min(p.x,p.y),-max(p.x,p.y))); // fuzzy logic
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise to-unit map thanks to Fabrice
    o = vec4(0);
    if(all(lessThan(abs(I), vec2(1))))
    {
        //I *= 2.0;
        //I = apply_moebius(vec2(1,0), vec2(0), vec2(0), vec2(1,0), I); // identity
        //I = apply_moebius(vec2(0), vec2(1,0), vec2(1,0), vec2(0), I); // inversion
        /*
        vec2 p0 = vec2(-1,-1), q0 = vec2(-1,-1);
        vec2 p1 = vec2(+1,-1), q1 = vec2(+1,-1);
        vec2 p2 = vec2(-1,+1), q2 = vec2(-1,+1);
        */
        vec2 p0 = vec2(-1,-1), q0 = vec2(+1,+1);
        vec2 p1 = vec2(+1,-1), q1 = vec2(-1,+1);
        //vec2 p2 = vec2(-1,+1), q2 = vec2(+1,-1);
        vec2 p2 = vec2(-1,+1), q2 = vec2(+3,-1);

        cmat2 m = moebius_by_triangles(p0, q0, p1, q1, p2, q2);
        //m = cmat2(vec2(0), vec2(1,0), vec2(1,0), vec2(0));
        I = apply_moebius(m, I);

        o.xyz = vec3(mix(0.25,0.75,checker(I)));
    }
}
