const int[18] ns = int[](-1, -1, 0, -1, 1, -1, -1, 0, 1, 0, 0, 0, -1, 1, 0, 1, 1, 1);

vec4 texel(in ivec2 x) {
    return texelFetch( iChannel0, x, 0 );
}

vec4 avg( in ivec2 px )
{

    vec4 sum = vec4(0.);

    for (int i = 0; i < 18; i += 2) {
       vec4 n = texel(px+ivec2(ns[i%18],ns[(i%18)+1]));
       sum += n;
    }

   return sum/9.;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 iv = ivec2(fragCoord);
    fragColor = avg(iv);
    fragColor = texel(iv);

}

vec3 pools(in vec2 uv)
{
    float dt  = .5+.5*sin(.16*iTime);
    float dt2 = .5+.5*sin(.48*iTime);
    float mdt = dt;
    float mdt2 = .7+.3*dt2;

    float dt3 = .5+.5*sin(.14*iTime);
    float dt4 = .5+.5*sin(.53*iTime);
    float mdt3 = dt3;
    float mdt4 = .7+.3*dt4;

    float dt5  = .5+.5*sin(.15*iTime);
    float dt6 = .5+.5*sin(1.5+.49*iTime);
    float mdt5 = dt5;
    float mdt6 = .7+.3*dt6;

    float dt7  = .5+.5*sin(.3*iTime);
    float mdt7 = .1+.9*dt7;

    float dt8  = .5+.5*sin(1.5+.2*iTime);
    float mdt8 = .9+.1*dt8;


    //uv = vec2(sin(25.*uv));

    float r2 = length(uv)*length(uv);

    vec3 c = vec3(0.);
    float a = 1.;
    float f = 12.;
    float as = 0.;
    for (int i = 0; i < 3; i++) {
        c.r += a*(mdt)*(.5+.5*sin(15.*mdt2+f*mdt2*2.2*uv.x));
        c.g += a*(mdt3)*(.5+.5*sin(11.*mdt4+f*mdt4*2.1*uv.y));
        c.b += a*(mdt5)*.7*length(sin(22.*mdt6+f*mdt6*1.4*uv));
        as += a;
        f *= 1.3+length(c);
        //f *= 1.33;
        a *= 1.2; 
    }
    c /= as;

    //c = vec3(uv.x, uv.y, uv.x/uv.y);  
    //c = vec3(1.+sin(21.*uv), 0.);

    for(int i = 0; i < 0; i++) {
        //uv = vec2(sin(uv.x), sin(uv.y));
        float r3 = length(c)*length(c)*length(c);
        //c = c*(1./(vec3(.4*mdt7+r3)));

        float k = 1.;
