#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+atan(r)*cross(p,a)
#define H(h)(sin((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.x,1));  

    for(float i=0.,a,s,e,g=0.;
        ++i<122.;
        O.xyz+=mix(vec3(1),H(g*.3),sin(.8))*1./e/8e3
    )
    {
        p=g*d;
        p.z+=iTime*6.5;
        a=12.;
        p=mod(p-a,a*2.)-a;
        s=8.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p; 
            p.y<p.y?p=p.yzx:p;

            s*=e=1.4+cos(iTime*.50)*.1;
            p=abs(p)*e-
                vec3(
                    5.+atan(iTime*.3+.5*tan(iTime*.3))*3.,
                    160,
                    8.+atan(iTime*.5)*5.

                 );
         }
        // g+=e=length(p.yz)/s;
         g+=e=length(p.zy)/s;
             g+=e=length(p.yz)/iTime;
    }
}
