vec3 lerp(vec3 in1, vec3 in2, float t){

    vec3 out1 = in1 * (1.0 - t);
    vec3 out2 = in2 * t;

    return out1 + out2;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col1 = vec3(2.0, 0.9, 0.3);
    vec3 col2 = vec3(1.0, 0.0, 1.6);

    fragColor = vec4(lerp(col1, col2, uv.y), 1.0);
}
