vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float a = atan(p.y, p.x);
    float r = length(p) * 5.25;

    float intensity = 0.5 + 0.5 * sin(iTime * 5.0 + r * 3.14);

    float dotSize = 0.00015;
    float dotSpacing = 0.08;
    float dotRotation = iTime * 0.001;

    vec2 dotPos = vec2(cos(dotRotation), sin(dotRotation)) * r * dotSpacing;
    float dotDistance = length(fract(dotPos / dotSize) - 0.5);

    float pattern = smoothstep(0.4, 0.0, dotDistance) * intensity;

    float hue = mod((r + iTime * 0.2) / 5.0, 1.0);
    vec3 color = hsv2rgb(vec3(hue, 1.0, 1.0));

    fragColor = vec4(color * pattern, 1.0);
}
