void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
	vec2 P = U-.5;
    float t = iTime;
	P =   P*P
        +     sin(U.x*18.+t    ) /25.
           *  sin(U.y*7. +t*1.5) 
        + dot( U, sin(t*vec2(1,.2)) )/16. ;

	float v = sqrt(abs(P.x*2.+P.y)) *5.;

    vec3 V = vec3(sin(v*1.25 +2.),
                  abs(sin(v-1.)-sin(v)),
                  abs(sin(v)) );

    v = dot(V, vec3(.5, .5, .1) ); 

    O = vec4( .7*v/fwidth(v) );
}
