// cosine based palette, 4 vec3 params
vec3 palette( float t )
{
    vec3 a = vec3(0.728, 0.490, 0.638);
    vec3 b = vec3(0.295, -0.874, 0.265);
    vec3 c = vec3(2.719, 0.910, 1.245);
    vec3 d = vec3(0.495, 0.625, 0.623);

    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
   vec2 uv0 = uv;
   vec3 finalColor = vec3(0.0);

   for(float i = 0.0; i < 5.0; i++) {

    uv = fract(uv * 1.5) - 0.5;

    float d = length(uv) * exp(-length(uv0 *.02));

    vec3 col = palette(length(uv0) + i*.4 + iTime*0.05);

    d = sin(d*6. + iTime * 2.0)/5.;
    d = abs(d);

    d = 0.0075 / d;

    finalColor += col * d;

   }


   fragColor = vec4(finalColor, 1.0);
}
