const int g_ThresholdMatrix4[4] = int[] (
    0, 2,
    3, 1
);

const int g_ThresholdMatrix16[16] = int[] (
	0,  8,  2,  10,
	12, 4,  14, 6,
	3,  11, 1,  9,
	15, 7,  13, 5
);

const int g_ThresholdMatrix64[64] = int[] (
    0,  32, 8,  40, 2,  34, 10, 42,
    48, 16, 56, 24, 50, 18, 58, 26,
    12, 44, 4,  36, 14, 46, 6,  38,
    60, 28, 52, 20, 62, 30, 54, 22,
    3,  35, 11, 43, 1,  33, 9,  41,
    51, 19, 59, 27, 49, 17, 57, 25,
    15, 47, 7,  39, 13, 45, 5,  37,
    63, 31, 55, 23, 61, 29, 55, 21
);

float ThresholdValue4( vec2 uv )
{
    int x = int( mod( uv.x, 2.0f ) );
    int y = int( mod( uv.y, 2.0f ) );
    return float( g_ThresholdMatrix4[( x + y * 2 )] ) / 4.0;
}

float ThresholdValue16( vec2 uv )
{
    int x = int( mod( uv.x, 4.0f ) );
    int y = int( mod( uv.y, 4.0f ) );
    return float( g_ThresholdMatrix16[( x + y * 4 )] ) / 16.0;
}

float ThresholdValue64( vec2 uv )
{
    int x = int( mod( uv.x, 8.0f ) );
    int y = int( mod( uv.y, 8.0f ) );
    return float( g_ThresholdMatrix64[( x + y * 8 )] ) / 64.0;
}

bool Dither4( float x, vec2 uv )
{
    return x < ThresholdValue4( uv );
}

bool Dither16( float x, vec2 uv )
{
    return x < ThresholdValue16( uv );
}

bool Dither64( float x, vec2 uv )
{
    return x < ThresholdValue64( uv );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float x = uv.x;
    vec3 color = vec3( float( Dither64( x, fragCoord ) ) );
    fragColor = vec4( color, 1.0 );
}
