// custom-less golfing 540 chars https://shadertoy.com/view/csj3RR
// custom-keep golfing 1084 chars https://shadertoy.com/view/DdjGRR
// simplifying Envy24's "Dynamic system plotter" https://shadertoy.com/view/fl3GW7
//    length 5476 - 2684 = 2792 chars ( only initForSystem0 )

void mainImage( out vec4 O, vec2 U )  { O = T(U); }

void mainImage( out vec4 O, vec2 P )  // 428
{
    O = T(P) + .022;
    P = ( P+P - R ) / R.y + vec2(.6, -.48);

    float s = .011, i = 0.,
          t = mix( .46, .472, .5+.5*sin(iTime*.07*O.x) ),      
          d = 9., x=t, y=t, _x;

    for ( ; i < 2e3; ++i )
        _x =   x*x + y*t - t*x - x  ,     
         y =  -y*y - y*t - t*x - y -x*y - t*t,  
         x = _x,
        d = min( d, length( P - vec2(x,y) ) );          

    O *= smoothstep(-s, s, d - .0035 );
}
