void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col = texture(iChannel0,uv).rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    uv = (uv+vec2(0,1))*5.2;
    vec3 col = vec3(0.75);
    float x = 0.0;
    float y = 0.0;
    float a,b,c,d,f;

    for(int i = 0; i < 1000; i++){

        float r = fract(sin(uv.x+float(i))*3141.516);

        if( r<0.02 ) { a=0.0; b=0.0; c=0.0; d=0.16; f=0.0; }

        else if( r<0.89 ) { a=0.85; b=0.04; c=-0.04; d=0.85; f=1.6; }

        else if( r<0.96 ) { a=0.2; b=-0.26; c=0.23; d=0.22; f=1.6; }

        else { a=-0.15; b=0.28; c=0.26; d=0.24; f=0.44; }

        float xs = x;

        x = a*x + b*y;

        y = c*xs + d*y + f;

        // plotPointAt( x, y );
        bool b = length(vec2(x,y)-uv)<0.1;
        if (b) {
            fragColor = vec4(1.0,0.0,1.0,1.0);
            return;
        }

    }
    fragColor = vec4(col,1.0);
}
