// Noise
float hash( float n ) {
    return fract(sin(n)*7586.5453);
}

float noise( in vec3 x ) { // in [0,1]
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.-2.*f);

    float n = p.x + p.y*57. + 113.*p.z;

    float res = mix(mix(mix( hash(n+  0.), hash(n+  1.),f.x),
                        mix( hash(n+ 57.), hash(n+ 58.),f.x),f.y),
                    mix(mix( hash(n+113.), hash(n+114.),f.x),
                        mix( hash(n+170.), hash(n+171.),f.x),f.y),f.x);
    return res;
}

float fbm(float x, float y) {
    float total = 0.0;
    float persistence = 0.5f;
    //int octaves = 8;
    float freq = 2.f;
    float amp = 0.5f;
    for(int i = 1; i <= 6; i++) {
        total += noise(vec3(x * freq, y * freq, 1.0)) * amp;

        freq *= 2.f;
        amp *= persistence;
    }
    return total;
}

float sfbm(in vec3 x) {
    return fbm(x.x, x.y) * 2.0 - 1.0;
}

float rippleNoise(float dist, float rippleIntensity, float rippleScale) {
    return rippleScale * (sin(dist * rippleIntensity - iTime * 10.0) / (dist * rippleIntensity));
}

// Colour changer function
vec3 maths(vec2 uv) {
    vec3 color = vec3(0.0);
      for(float i = 0.0; i < 128.0; i+=1.0)
      {
        float phase = sin(i*546.13 + 1.0)*0.5 + 0.5;
        float size = pow( sin(i*651.74 + 5.0)*0.5 + 0.5, 4.0 );
        float pox =  sin(i*0.55 + 4.1) * iResolution.x / iResolution.y;
        float rad = size + sin(phase+size)/1.5;
        vec2  pos = vec2( pox + sin(iTime/20.+ phase + size), -1.0-rad + (2.0+3.0*rad)*mod(phase + 0.2*(iTime/8.) * (0.5+size),1.0));
        float dis = length(uv - pos);
        vec3  col = mix( vec3(0.194*sin(iTime/6.0)+0.3,0.2,0.3*phase), vec3(1.1*sin(iTime/9.0)+0.3,0.2*phase,0.4), 0.5+0.5*sin(i));
        float f = length(uv-pos)/rad;
        f = sqrt(clamp(1.0 + (sin((iTime)*size)*0.5)*f,0.0,1.0));
        color += col.zyx *(1.0-smoothstep( rad*0.15, rad, dis ));
      }
      color *= sqrt(1.5-0.5*length(uv));
      return color;
}

// Main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = fragCoord.xy / iResolution.xy;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(1.0);

    col = maths(uv);

    for (float i = 0.0; i < 32.0; i+=1.0) {
        float d = 1.5 * sfbm(0.05 * vec3(fbm(uv.x, 0.01*(cos(iTime)) * uv.y)));
        d = abs(d);
        d = 0.1 / d;
        col *= pow(hash(d), 0.4);
    }

    col *= sqrt(1.5 - 0.5*length(uv));

    // Apply a ripple shader.
    float intensity = 0.0;
    for (int x = 0; x < 2; x++) {
        for (int y = 0; y < 2; y++) {
            // Random place on screen space?
            vec2 centre = vec2(0.375 + float(x) * 0.25, 0.375 + float(y) * 0.25);
            float dist = distance(col.xy, centre);
            intensity += rippleNoise(dist, 50.  + 100. * float(x), 2.5 + 5. * float(y));
        }
    }
    intensity *= 4.5;

    fragColor = vec4(col.x * intensity, col.y * intensity, col.z * intensity,1.0);
}
