void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //fragColor = vec4(getBloom(uv),1.0);
    fragColor = texture(iChannel0,uv);
}

float sdHeart(vec2 p)
{
    float x = p.x;
    float y = p.y;
    return pow(x*x+y*y-1.0,3.0)-pow(x,2.0)*pow(y,3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 col = vec3(p,0.0);
    float x = p.x*abs(sin(iTime))*2.0;
    float y = p.y*abs(sin(iTime))*2.0;
    float d = sdHeart(vec2(x,y));
    col = (d<=0.0)? mix(vec3(1.0,0.5,0.0), vec3(1.0,0.2,0.0),1.0-y):vec3(0.0,0.0,0.0);
    fragColor = vec4(col,1.0);
}
