void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    //vec2 a = vec2(0.3, 0.3);
    vec2 a = iMouse.xy / iResolution.y;
    vec2 b = vec2(0.2, 0.5);
    vec2 c = uv;

    vec2 ab = b - a;
    vec2 ac = c - a;
    vec2 ab_hat = normalize(ab);
    vec2 d = dot(ac,ab_hat) * ab_hat + a;
    d = max(d, min(a, b));
    d = min(d, max(a, b));
    //d.y = min(d.y, max(a.y, b.y));
    float dist = distance(c, d);
    vec3 white = vec3(1.0, 1.0, 1.0);
    //vec3 col = white * float(dist >= 0.01);
    vec3 col = smoothstep(0.005, .005+(3.0/iResolution.y), dist) *  white;

    fragColor = vec4(col,1.0);
}
