void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float time = 0.5 + 0.5*cos(iTime);
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= (iResolution.y/iResolution.x);
    float dist = sdHorseshoe(uv-vec2(0.55,0.3), vec2(0.5, 0.5), 0.1, vec2(0.05, 0.05));
    float dist2 = sdCircle(uv-vec2(0.55,0.3),0.1);
    float interpolated = mix(dist, dist2, time);
    vec3 mixedColor = mix(vec3(1.0 * ((iResolution.xy - fragCoord) / fragCoord), 0.0), vec3(0.0, 1.0 * ((iResolution.xy - fragCoord) / fragCoord)), time);

    vec3 col = mix(mixedColor, vec3(0.0, 1.0 * ((iResolution.xy - fragCoord) / fragCoord)), step(0.0, interpolated));
    // Output to screen
    fragColor = vec4(col,1.0);
}
