void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,uv.x,uv.y-iResolution.x));
    float dfgh = uv.x+uv.y/2.;
    col = vec3(mod(col,dfgh));

    // Output to screen
    fragColor = vec4(col,mod(iTime,1.));
}

vec2 mainSound( int samp, float time )
{
float a = mod(time,.1);
float b = mod(time,.2);
float c = mod(time,.3);
float d = mod(time,.4);
float e = mod(time,.5);
float f = mod(time,.6);
float g = mod(time,.7);
float h = mod(time,.8);
float i = mod(time,.9);
float aj = a*b+c*d+e*f+g*h+i;
float ajf = a+b*c+d*e+f*g+h*i;
aj = aj+ajf;
float sd = 7.+5.+3.;
float sjd = aj*7.;
float idjf = sjd-sd;
aj = idjf/aj;
    return vec2( sin(6.2831*aj)*exp(-3.) );
}
